import{_ as p,c as t,o as s,a as o}from"./app.89540911.js";var r="/assets/001.2cdadf89.png",e="/assets/002.a3584093.png",n="/assets/003.8ee3c790.png",i="/assets/004.386123db.png",l="/assets/005.ecc1d215.png";const v=JSON.parse('{"title":"\u4EE3\u7801\u7684\u7B80\u5355\u8BBE\u8BA1\u4E94\u539F\u5219","description":"","frontmatter":{},"headers":[],"relativePath":"files/\u8F6F\u4EF6\u8BBE\u8BA1/\u4EE3\u7801\u7B80\u5355\u8BBE\u8BA1\u4E94\u539F\u5219.md"}'),a={name:"files/\u8F6F\u4EF6\u8BBE\u8BA1/\u4EE3\u7801\u7B80\u5355\u8BBE\u8BA1\u4E94\u539F\u5219.md"},g=o('<h1 id="\u4EE3\u7801\u7684\u7B80\u5355\u8BBE\u8BA1\u4E94\u539F\u5219" tabindex="-1">\u4EE3\u7801\u7684\u7B80\u5355\u8BBE\u8BA1\u4E94\u539F\u5219 <a class="header-anchor" href="#\u4EE3\u7801\u7684\u7B80\u5355\u8BBE\u8BA1\u4E94\u539F\u5219" aria-hidden="true">#</a></h1><p>\u6B22\u6B22\uFF1A\u201C\u4F60\u770B\u6211\u7684\u4EE3\u7801\u7528\u4E86\u7B56\u7565\u6A21\u5F0F\u548C\u72B6\u6001\u6A21\u5F0F\uFF0C\u5047\u5982\u540E\u9762\u5BA2\u6237\u4F1A\u6709\u8FD9\u6837\u7684\u9700\u6C42\uFF0C\u53EF\u4EE5\u65E0\u7F1D\u6269\u5C55\uFF0C\u591A\u4E48\u5065\u58EE\uFF01\u201D \u6E05\u626C\u4E00\u8138\u72D0\u7591\uFF0C\u5FC3\u4E2D\u5FF5\u53E8\u4E86\u6570\u904D \uFF1A\u201C\u54FC\uFF0C\u8FC7\u5EA6\u8BBE\u8BA1\uFF01\u201D\uFF0C\u53EA\u89C1\u5979\u6B32\u8A00\u53C8\u6B62\uFF0C\u597D\u51E0\u6B21\u8BDD\u5230\u5634\u8FB9\u53C8\u88AB\u81EA\u5DF1\u54BD\u56DE\u53BB\u4E86\u3002</p><p>\u8FD9\u79CD\u5173\u4E8E\u8BBE\u8BA1\u7684\u8BA8\u8BBA\uFF0C\u8881\u5E05\u6700\u8FD1\u4E00\u5468\u4E0D\u662F\u7B2C\u4E00\u6B21\u542C\u5230\u4E86\uFF0C\u5C31\u5728\u6628\u5929\u4ED6\u8FD8\u770B\u5230\u6E05\u626C\u548C\u6B63\u4E49\u7684\u4E00\u6B21\u53E3\u6C34\u4ED7\u3002\u6700\u8FD1\u6E05\u626C\u6709\u70B9\u4ED5\u9014\u4E0D\u987A\uFF0C\u51E0\u6B21\u88AB\u7ED3\u5BF9\u7684\u642D\u6863\u603C\u5F97\u65E0\u8A00\u4EE5\u5BF9\u3002\u8881\u5E05\u60F3\u4E3A\u4ED6\u7684Buddy\u6E05\u626C\u201C\u8BA8\u56DE\u516C\u9053\u201D\uFF0C\u4F46\u4E0D\u662F\u76F4\u63A5\u51FA\u9762\u5E2E\u6E05\u626C\u603C\u56DE\u53BB\u3002</p><p><strong>\u8BBE\u8BA1\u7684\u6807\u51C6\u5728\u54EA\u91CC\uFF1F</strong></p><p>\u5468\u4E94\u4E0B\u5348\uFF0C\u516C\u53F8\u5185\u90E8\u7684\u654F\u6377\u5DE5\u7A0B\u5B9E\u8DF5\u6307\u5BFC\u624B\u518C\u4E0A\u9192\u76EE\u7684\u4E09\u6761\u4EF7\u503C\u89C2\u7684\u300C<strong>\u7B80\u5355\u6027</strong>\u300D\u8BA9\u8881\u5E05\u9677\u5165\u4E86\u6C89\u601D\uFF1A</p><p><strong>\u7B80\u5355\u6027\uFF1A\u6211\u4EEC\u91CD\u89C6\u521A\u521A\u591F\u7528\u7684\u8BBE\u8BA1\u3002\u53EA\u4E3A\u5F53\u4E0B\u8BBE\u8BA1\uFF0C\u4E0D\u4E3A\u672A\u6765\u53EF\u80FD\u51FA\u73B0\u7684\u9700\u6C42\u505A\u8BBE\u8BA1\u3002\u4F46\u662F\uFF0C\u6211\u4EEC\u505A\u51FA\u7684\u51B3\u7B56\u5E94\u5F53\u5141\u8BB8\u8F6F\u4EF6\u5FEB\u901F\u53D8\u66F4\uFF0C\u80FD\u591F\u5FEB\u901F\u54CD\u5E94\u9700\u6C42\u53D8\u5316\u3002</strong></p><p>\u300C\u7B80\u5355\u6027\u300D\uFF0C\u770B\u8D77\u6765\u4E5F\u4F3C\u4E4E\u660E\u767D\u5728\u8BB2\u4EC0\u4E48\uFF0C\u53EF\u4EC0\u4E48\u662F\u521A\u521A\u591F\u7528\u7684\u8BBE\u8BA1\u5462\uFF1F\u8FD9\u53E5\u8BDD\u8BA9\u4ED6\u60F3\u8D77\u6765\u5F53\u5E74\u4ED6\u5728\u4E00\u6B21\u9762\u5411\u5BF9\u8C61\u8BAD\u7EC3\u8425\u7684\u8BFE\u540E\u8DDF\u67D0\u4E2A\u5B66\u5458\u8BF4\u8FC7\u7684\u4E00\u53E5\u8BDD\uFF1A\u201C\u8BBE\u8BA1\u72B9\u5982\u897F\u7EA2\u67FF\u7092\u9E21\u86CB\uFF0C\u76D0\u8981\u6070\u5230\u597D\u5904\u3002\u201D \u8FD9\u53E5\u8BDD\u662F\u5982\u6B64\u7684\u6B63\u786E\u5374\u53C8\u65E0\u6BD4\u7A7A\u6D1E\u3002</p><p>\u8BBE\u8BA1\u7684\u597D\u574F\u672C\u8EAB\u6CA1\u6709\u4E00\u4E2A\u6807\u51C6\u7684\u7B54\u6848\uFF0C\u8FD9\u4E48\u591A\u5E74\uFF0C\u8881\u5E05\u4E5F\u5728\u8DDF\u7740\u8F6F\u4EF6\u754C\u5404\u8DEF\u795E\u4ED9\u5B66\u4E60\u8BBE\u8BA1\u539F\u5219\uFF0C\u4ECD\u7136\u5904\u5728\u4F3C\u61C2\u975E\u61C2\u7684\u72B6\u6001\u3002\u4ED6\u4E5F\u6DF1\u77E5\u6BCF\u4E2A\u4EBA\u5FC3\u4E2D\u90FD\u6709\u4E00\u6746\u79E4\u3002**\u4EC0\u4E48\u662F\u597D\u7684\u8BBE\u8BA1\uFF1F**\u4FBF\u6210\u4E86\u516C\u8BF4\u516C\u6709\u7406\uFF0C\u5A46\u8BF4\u5A46\u6709\u7406\u7684\u95EE\u9898\uFF0C\u8C01\u4E5F\u96BE\u4EE5\u8BF4\u670D\u8C01\u3002</p><p>\u8FD9\u4E00\u6B21\u4ED6\u4E0D\u592A\u60F3\u63D0\u90A3\u4E9B\u7A7A\u7A7A\u5982\u4E5F\u7684\u4E1C\u897F\uFF0C\u4E3A\u4E86\u80FD\u591F\u8BA9\u6E05\u626C\u5FEB\u901F\u638C\u63E1\u8981\u70B9\uFF0C\u4ED6\u5C1D\u8BD5\u5C06\u8303\u56F4\u7F29\u5C0F\u5230\u654F\u6377\u56E2\u961F\u7A0B\u5E8F\u5458\u4EA4\u4ED8\u7528\u6237\u6545\u4E8B\u5361\u65F6\u7684\u7F16\u7801\u8BBE\u8BA1\uFF0C\u907F\u5F00\u67B6\u6784\u8BBE\u8BA1\u3002\u4ECE\u53D8\u91CF\u3001\u5E38\u91CF\u3001\u65B9\u6CD5\u3001\u7C7B\u3001\u7C7B\u4E0E\u7C7B\u4E4B\u95F4\u7684\u5173\u7CFB\u3001\u5BF9\u8C61\u7684\u4EA4\u4E92\u5F00\u59CB\u3002</p><p><strong>\u91CD\u6E29\u65E7\u6587\uFF1A\u7B80\u5355\u8BBE\u8BA1</strong></p><p>\u5468\u516D\uFF0C\u8881\u5E05\u5F88\u65E9\u5C31\u94BB\u5230\u4E66\u623F\uFF0C\u70B9\u71C3\u80CC\u666F\u97F3\u4E50\u300A\u7A3B\u9999\u300B\uFF0C\u6253\u5F00\u535A\u5BA2\u4E3B\u9875\uFF0C\u53D1\u73B0\u4E86\u4E00\u7BC7\u591A\u5E74\u524D\u5199\u7684\u6587\u7AE0\u300A\u7B80\u5355\u8BBE\u8BA1\u300B\uFF0C\u4ED4\u7EC6\u901A\u8BFB\u4E00\u904D\u4E4B\u540E\uFF0C\u4ED6\u89C9\u5F97\u8FD8\u4E0D\u9519\uFF0C\u53EF\u4EE5\u4F5C\u4E3A\u5165\u95E8\uFF0C\u53D1\u7ED9\u6E05\u626C\u9605\u8BFB\uFF0C\u7EA6\u4E0B\u5468\u4E00\u5927\u4E00\u65E9\u53BB\u516C\u53F8\u8BA8\u8BBA\u3002</p><p>\u4ED6\u82B1\u4E86\u8FD1\u4E00\u4E2A\u5C0F\u65F6\u5C06\u6587\u5B57\u6DA6\u8272\uFF0C\u4E5F\u57FA\u4E8E\u6700\u8FD1\u5BF9\u8BBE\u8BA1\u7684\u4F53\u4F1A\u8C03\u6574\u4E86\u90E8\u5206\u5185\u5BB9\uFF0C\u4FDD\u7559\u4E86\u6587\u7AE0\u7684\u6574\u4F53\u8109\u7EDC\u3002</p><p><strong>\u7528\u5177\u4F53\u7684\u8BCD\u6C47\u8868\u8FBE\u8BBE\u8BA1</strong></p><p>\u62BD\u8C61\u7684\u8BBE\u8BA1\u95EE\u9898\u5927\u5927\u63D0\u5347\u4E86\u521D\u5B66\u8005\u7684\u5B66\u4E60\u95E8\u69DB\uFF0C\u60F3\u5F97\u592A\u591A\u6015\u88AB\u8BF4\u8FC7\u5EA6\u8BBE\u8BA1\uFF0C\u5403\u9971\u6491\u7740\u6CA1\u4E8B\u627E\u4E8B\u3002\u60F3\u5C11\u4E86\uFF0C\u53C8\u6015\u88AB\u4EBA\u8BA4\u4E3A\u80FD\u529B\u4E0D\u8DB3\uFF0C\u65E0\u8111\u7F16\u7801\u3002\u5230\u5E95\u600E\u4E48\u529E\uFF0C\u600E\u4E48\u6837\u624D\u80FD\u505A\u51FA\u597D\u7684\u8BBE\u8BA1\uFF1FSOLID\u3001GoF\u768423\u79CD\u8BBE\u8BA1\u6A21\u5F0F\u3001STUPID\u3001GRASP\u8FD9\u4E9B\u539F\u5219\u5B66\u4F1A\u4E86\u5C31\u53EF\u4EE5\u4E86\u5417\uFF1FNo\uFF0C\u7EDF\u7EDF\u5FD8\u6389\u8FD9\u4E9B\u62BD\u8C61\u4E0D\u63A5\u5730\u6C14\u7684\u8BBE\u8BA1\u539F\u5219\u3002</p><p>\u8D77\u6B65\uFF0C\u5C3D\u91CF\u522B\u4E3A\u96BE\u81EA\u5DF1\u3002\u6781\u9650\u7F16\u7A0B\u9886\u57DF\u7684\u5927\u5E08\u7A0B\u5E8F\u5458Kent Beck\u5F88\u65E9\u524D\u5C31\u63D0\u51FA\u4E864\u6761\u76F8\u5BF9\u5BB9\u6613\u7406\u89E3\u7684\u53C2\u8003\u539F\u5219\uFF1A</p><p><img src="'+r+'" alt="\u56FE\u7247"></p><p><strong>\u539F\u5219\u4E00\uFF1A\u901A\u8FC7\u6D4B\u8BD5\uFF08\u4FE1\u4EF0\uFF09</strong></p><p><strong>\u300C\u901A\u8FC7\u6D4B\u8BD5\u300D</strong> \u901A\u5E38\u4F1A\u88AB\u4E00\u6982\u5730\u7406\u89E3\u4E3A\u901A\u8FC7\u81EA\u5DF1\u5728\u9879\u76EE\u4E2D\u7684\u5404\u79CD\u6D4B\u8BD5\uFF08\u81EA\u52A8\u5316 + \u624B\u5DE5\uFF09\uFF0C\u8FD9\u4E48\u7406\u89E3\uFF0C\u4E5F\u6CA1\u6709\u4EC0\u4E48\u95EE\u9898\uFF0C\u4F46\u662F\u9700\u8981\u6EE1\u8DB3\u4E24\u4E2A\u524D\u63D0\u6761\u4EF6\uFF1A</p><ol><li>\u6D4B\u8BD5\u8986\u76D6\u7387\u8FBE\u5230100%</li><li>\u6240\u6709\u6D4B\u8BD5\u90FD\u662F\u6709\u6548\u7684</li></ol><p>\u5982\u679C\u4F60\u7684\u9879\u76EE\u4E2D\u6CA1\u6CD5\u6EE1\u8DB3\u8FD9\u4E24\u70B9\uFF0C\u5F53\u7136\uFF0C99%\u7684\u9879\u76EE\u662F\u505A\u4E0D\u5230\u7684\uFF08\u8FD8\u67091%\u5B58\u5728\u4F20\u8BF4\u4E2D\uFF09\u3002\u6B64\u65F6\u4F60\u9700\u8981\u6362\u4E00\u4E2A\u89D2\u5EA6\u53BB\u7406\u89E3 <em>\u901A\u8FC7\u6D4B\u8BD5</em>\u3002</p><p>\u4F60\u4E3A\u4EC0\u4E48\u5199\u6D4B\u8BD5\uFF1F\u6D4B\u8BD5\u5728\u6D4B\u4EC0\u4E48\uFF1F\u4E0D\u5C31\u662F\u4E3A\u4E86\u589E\u5F3A\u4F60\u5BF9\u7CFB\u7EDF\u529F\u80FD\u662F\u5426\u6EE1\u8DB3\u4E86\u4E1A\u52A1\u9700\u6C42\u7684\u4FE1\u5FC3\u5417\uFF1F\u6240\u4EE5\u300C<strong>\u901A\u8FC7\u6D4B\u8BD5</strong> \u300D\u5E7F\u4E49\u7406\u89E3\u4E3A\u8981\u6EE1\u8DB3\u4E1A\u52A1\u9700\u6C42\uFF0C\u4E0D\u8BBA\u662F\u81EA\u52A8\u5316\u6D4B\u8BD5\u8FD8\u662F\u624B\u5DE5\u6D4B\u8BD5\uFF0C\u4F60\u9700\u8981\u505A\u7684\u662F\u6EE1\u8DB3\u4E1A\u52A1\u9700\u6C42\uFF0C\u53EA\u4E0D\u8FC7\u6211\u4EEC\u63D0\u5021\u5C3D\u53EF\u80FD\u7F16\u5199\u8DB3\u591F\u7684\u81EA\u52A8\u5316\u56DE\u5F52\u6D4B\u8BD5\u3002</p><p><strong>\u539F\u5219\u4E8C\uFF1A\u6D88\u9664\u91CD\u590D\uFF08\u804C\u8D23\uFF09</strong></p><p>\u201C</p><blockquote><p>\u91CD\u590D\u4E43\u4E07\u6076\u4E4B\u6E90\u2014\u2014Kent Beck \u6CA1\u6709\u8BF4\u8FC7</p></blockquote><p>\u201D</p><p>\u91CD\u590D\u610F\u5473\u7740\u4F4E\u5185\u805A\u3001\u9AD8\u8026\u5408\uFF0C\u5BFC\u81F4\u7684\u540E\u679C\u662F\u96BE\u4EE5\u4FEE\u6539\uFF08\u9730\u5F39\u5F0F\u4FEE\u6539\uFF09\uFF0C\u5FC5\u7136\u964D\u4F4E\u7CFB\u7EDF\u5BF9\u53D8\u5316\u7684\u54CD\u5E94\u529B\u3002\u54CD\u5E94\u529B\u7684\u964D\u4F4E\u52BF\u5FC5\u4F1A\u9020\u6210\u7EF4\u62A4\u5DE5\u4F5C\u91CF\u7684\u63D0\u5347\uFF0C\u6211\u7684\u7B80\u5355\u8BBE\u8BA1\u4EF7\u503C\u89C2 \u4E00\u6587\u4E2D\u7684 <em>\u61D2\u60F0</em> \u5C06\u9A71\u4F7F\u6211\u5C3D\u6211\u6240\u80FD\u6D88\u9664\u8FD9\u4E9B\u91CD\u590D\uFF0C\u4ECE\u800C\u51CF\u5C11\u4FEE\u6539\u65F6\u7684\u5DE5\u4F5C\u91CF\uFF0C\u63D0\u5347\u8F6F\u4EF6\u7684\u54CD\u5E94\u529B\u3002</p><p><strong>\u539F\u5219\u4E09\uFF1A\u63ED\u793A\u610F\u56FE\uFF08\u521D\u8877\uFF09</strong></p><p>\u63ED\u793A\u610F\u56FE\uFF0C\u542C\u8D77\u6765\u662F\u4E00\u4E2A\u4E0D\u53EF\u8A00\u8BF4\u7684\u6982\u5FF5\uFF0C\u600E\u6837\u8868\u793A\u63ED\u793A\u610F\u56FE\u4E86\u5462\uFF1F\u5BF9\u4E8E\u8FD9\u4E00\u6761\uFF0C\u6211\u4EEC\u5F88\u96BE\u6709\u4E00\u4E2A\u6807\u51C6\u4E14\u5B8C\u7F8E\u7684\u7B54\u6848\uFF0C\u505A\u4E0D\u5230\u5B8C\u7F8E\uFF0C\u4F46\u4E0D\u59A8\u788D\u6211\u4EEC\u52AA\u529B\u5C1D\u8BD5\u8D8B\u8FD1\u5B8C\u7F8E\u3002</p><p>\u4F60\u53EF\u4EE5\u5728\u7F16\u7801\u8FC7\u7A0B\u4E2D\uFF0C\u4E0D\u65AD\u95EE\u81EA\u5DF1\uFF1A\u4EE3\u7801\u5BB9\u6613\u7406\u89E3\u5417\uFF1F\u5B83\u6709\u6CA1\u6709\u504F\u79BB\u5B83\u7684\u521D\u8877\uFF08\u4E1A\u52A1\u9700\u6C42\uFF09\uFF1F\u7D27\u63A5\u7740\uFF0C\u8FDB\u4E00\u6B65\u63A2\u7D22\u8FD9\u80CC\u540E\u66B4\u9732\u7684\u884C\u4E3A\u4FE1\u53F7 -- \u300C\u89E3\u91CA\u300D\uFF1A</p><ol><li>\u65B0\u4EBA\u4E86\u89E3\u4E86\u4E1A\u52A1\u9700\u6C42\u540E\uFF0C\u80FD\u591F\u7B2C\u4E00\u65F6\u95F4\u6E05\u6670\u5730\u4ECE\u4EE3\u7801\u4E2D\u627E\u5230\u5BF9\u5E94\u7684\u4EE3\u7801\u5417\uFF1F</li><li>\u4F60\u9700\u8981\u989D\u5916\u5BF9\u4E00\u4E2A\u65B0\u4EBA\u89E3\u91CA\u4EE3\u7801\u7684\u542B\u4E49\u5417\uFF1F\u5982\u679C\u8981\uFF0C\u4F60\u8981\u89E3\u91CA\u5230\u4EC0\u4E48\u7A0B\u5EA6\uFF1F</li></ol><p>\u8FD9\u51E0\u4E2A\u95EE\u9898\u4F1A\u8BA9\u4F60\u4E0D\u65AD\u53CD\u601D\u4F60\u7684\u4EE3\u7801\u80FD\u591F\u4F53\u73B0\u4E1A\u52A1\u521D\u8877\u5417\uFF1F\u53D8\u91CF\u3001\u65B9\u6CD5\u4EE5\u53CA\u7C7B\u7684\u547D\u540D\u7B49\uFF0C\u4F60\u65F6\u523B\u90FD\u4FDD\u6301\u8B66\u60D5\u7684\u662F\uFF1A**\u8D4B\u4E88\u5B83\u4E00\u4E2A\u66F4\u52A0\u51C6\u786E\u8868\u8FBE\u4E1A\u52A1\u7684\u540D\u5B57\uFF0C\u4E00\u4E2A\u4E0D\u8981\u989D\u5916\u89E3\u91CA\u7684\u540D\u5B57\u3002**\u4ECE\u800C\u8BA9\u8BFB\u8005\u80FD\u591F\u5728\u6DF1\u5165\u7EC6\u8282\u4E4B\u524D\u5C31\u80FD\u591F\u5728\u8F83\u9AD8\u5C42\u6B21\u4E0A\u5FEB\u901F\u7406\u89E3\u4EE3\u7801\u7684\u610F\u56FE\u3002</p><p><strong>\u539F\u5219\u56DB\uFF1A\u6700\u5C11\u5143\u7D20\uFF08\u7CBE\u9AD3\uFF09</strong></p><p>\u65E2\u7136\u8BF4\u7684\u662F\u4EE3\u7801\uFF0C\u90A3\u4E48\u5145\u65A5\u5728\u4F60\u7684\u4EE3\u7801\u5E93\u4E2D\u7684\u4EFB\u4F55\u4E1C\u897F\u90FD\u53EF\u4EE5\u7406\u89E3\u662F\u5143\u7D20\u3002\u5F53\u7136\uFF0C\u6211\u4EEC\u8FD8\u662F\u7126\u70B9\u805A\u7126\u5728\u4E0E\u4EE3\u7801\u76F8\u5173\u7684\u5143\u7D20\uFF0C\u6BD4\u5982\uFF0C\u53D8\u91CF\u3001\u5E38\u91CF\u3001\u6CE8\u91CA\u3001\u6CE8\u89E3\u3001\u5173\u952E\u5B57\u3001\u5305\u3002</p><p>\u300C<strong>\u6700\u5C11\u5143\u7D20</strong>\u300D \u7684\u6838\u5FC3\u601D\u60F3\u662F\uFF1A\u5728\u4E0D\u5FC5\u8981\u7684\u65F6\u5019\uFF0C\u5C3D\u53EF\u80FD\u51CF\u5C11\u4EE3\u7801\u5143\u7D20\u6765\u964D\u4F4E\u4EE3\u7801\u590D\u6742\u5EA6\uFF0C\u4FDD\u6301\u7B80\u6D01\uFF0C\u8D2F\u5F7Bless is more\u7684\u601D\u60F3\uFF0C\u5B83\u9053\u51FA\u4E86\u7B80\u5355\u8BBE\u8BA1\u7684\u7CBE\u9AD3\u3002</p><p><strong>\u539F\u5219\u4E94\uFF1A\u524D\u56DB\u6761\u4F18\u5148\u7EA7\u4F9D\u6B21\u964D\u4F4E\uFF08\u7075\u9B42\uFF09</strong></p><p>\u7B80\u5355\u8BBE\u8BA1\u524D\u56DB\u6761\u539F\u5219\u7ED9\u8BBE\u8BA1\u51B3\u7B56\u63D0\u4F9B\u4E86\u6307\u5BFC\uFF0C\u5728\u5B9E\u9645\u8FD0\u7528\u8FC7\u7A0B\u4E2D\uFF0C\u5F53\u9762\u4E34\u51B2\u7A81\u65F6\uFF0C\u6211\u4EEC\u5982\u4F55\u53D6\u820D\uFF0CKent Beck\u4E5F\u63D0\u51FA\u4E00\u4E2A\u4F18\u5148\u7EA7\uFF1A\u901A\u8FC7\u6D4B\u8BD5 &gt; \u6D88\u9664\u91CD\u590D &gt;= \u63ED\u793A\u610F\u56FE &gt; \u6700\u5C11\u5143\u7D20\u3002</p><p>\u4EE5\u4E0A\u56DB\u6761\u4F18\u5148\u7EA7\u4F9D\u6B21\u964D\u4F4E\uFF0C\u8FD9\u5C31\u8BDD\u6709\u70B9\u7C7B\u4F3C\u654F\u6377\u5BA3\u8A00\u4E2D\u7684\u6700\u540E\u4E00\u53E5\uFF1A\u4E5F\u5C31\u662F\u8BF4\uFF0C\u5C3D\u7BA1\u53F3\u9879\u6709\u5176\u4EF7\u503C\uFF0C\u6211\u4EEC\u66F4\u91CD\u89C6\u5DE6\u9879\u7684\u4EF7\u503C\u3002</p><ol><li>\u901A\u8FC7\u6D4B\u8BD5</li><li>\u6D88\u9664\u91CD\u590D</li><li>\u63ED\u793A\u610F\u56FE</li><li>\u6700\u5C11\u5143\u7D20</li><li><strong>\u4EE5\u4E0A\u56DB\u6761\u4F18\u5148\u7EA7\u4F9D\u6B21\u964D\u4F4E</strong></li></ol><p><strong>\u4F18\u5148\u7EA7\u5E2E\u52A9\u63ED\u5F00\u8FF7\u96FE</strong></p><p>\u5468\u672B\u8FC7\u7684\u98DE\u5FEB\uFF0C\u6E05\u626C\u8BFB\u5B8C\u4E86\u8881\u5E05\u53D1\u7ED9\u4ED6\u7684\u300A\u7B80\u5355\u8BBE\u8BA1\u300B\uFF0C\u800C\u4E14\u8BFB\u4E86\u5F88\u591A\u904D\uFF0C\u4E00\u8111\u5B50\u7684\u7591\u95EE\u7B49\u7740\u8981\u627E\u8881\u5E05\u63A2\u8BA8\u3002\u5979\u6BD4\u5F80\u65E5\u63D0\u524D\u4E86\u4E00\u4E2A\u5C0F\u65F6\u5230\u4E86\u529E\u516C\u5BA4\uFF0C\u53EA\u89C1\u8881\u5E05\u5DF2\u7ECF\u5728\u5DE5\u4F4D\uFF0C\u4E00\u526F\u5C31\u7B49\u5979\u6765\u6218\u7684\u6001\u52BF\u3002</p><p>\u8FD8\u6CA1\u7B49\u6E05\u626C\u5F00\u53E3\uFF0C\u8881\u5E05\u9012\u7ED9\u5979\u56DB\u5F20\u7EA2\u8272\u5361\u7247\uFF0C\u662F\u4ED6\u7684\u624B\u5199\u7B14\u8BB0\uFF0C\u6E05\u626C\u89C1\u5B57\u8FF9\u5DE5\u6574\uFF0C\u8C8C\u4F3C\u5979\u4ECE\u672A\u89C1\u8FC7\u8881\u5E05\u5982\u6B64\u8BA4\u771F\u5199\u8FC7\u5B57\uFF0C\u9887\u611F\u610F\u5916\u548C\u611F\u52A8\uFF0C\u683C\u5916\u8BA4\u771F\u5730\u9605\u8BFB\u8D77\u6765\u3002</p><p><img src="'+e+'" alt="\u56FE\u7247"></p><p>\u201C\u6E05\u626C\uFF0C\u8003\u4F60\u4E00\u4E2A\u8111\u7B4B\u6025\u8F6C\u5F2F -- \u5728\u5DE5\u4F5C\u4E2D\u4F60\u7684\u9886\u5BFC\u7684\u9886\u5BFC\u7684\u9886\u5BFC\u7684\u9886\u5BFC\uFF084\u4E2A\u4EBA\uFF09\uFF0C\u5F53\u4ED6\u4EEC\u7ED9\u4F60\u7684\u6307\u4EE4\u6709\u51B2\u7A81\u65F6\uFF0C\u4F60\u8BE5\u542C\u8C01\u7684\uFF1F\u201D\u3002\u201C\u5F53\u7136\u662F\u542C\u66F4\u9AD8\u7EA7\u9886\u5BFC\u7684\u6307\u4EE4\u554A\uFF01\u201D\u6E05\u626C\u6761\u4EF6\u53CD\u5C04\u5F0F\u5FEB\u901F\u56DE\u7B54\u5230\u3002</p><p>\u8881\u5E05\u89C1\u72B6\u4F1A\u5FC3\u4E00\u7B11\uFF0C\u6E05\u626C\u4E5F\u6320\u6320\u5934\u8C8C\u4F3C\u660E\u767D\u8881\u5E05\u7684\u610F\u601D\u3002\u201C\u53EF\u662F\uFF0C\u6211\u8FD8\u662F......\u201D \u8FD8\u6CA1\u7B49\u6E05\u626C\u8BF4\u5B8C\uFF0C\u8881\u5E05\u793A\u610F\u5979\u9760\u8FD1\u6765\u770B\u4ED6\u65E9\u65E9\u51C6\u5907\u597D\u7684\u4EE3\u7801\u3002</p><p><strong>Talk is cheap</strong></p><p>\u793A\u4F8B\u4E00\uFF1A</p><p><img src="'+n+'" alt="\u56FE\u7247"></p><p>\u8881\u5E05\u5FEB\u901F\u51FA\u62DB\uFF1A\u201C\u8FD9\u6BB5\u4EE3\u7801\u5728\u505A\u4EC0\u4E48\uFF1F\u7528\u7B80\u5355\u8BBE\u8BA1\u6846\u67B6\u600E\u4E48\u89E3\u8BFB\uFF1F\u201D\u3002\u6E05\u626C\u654F\u6377\u5730\u63A5\u62DB\uFF1A\u201C\u62BD\u53D6\u516C\u5171\u65B9\u6CD5\uFF0C\u4E3A\u4E86\u300C\u6D88\u9664\u91CD\u590D\u300D\u800C\u8FDD\u80CC\u300C\u6700\u5C11\u5143\u7D20\u300D\u3002\u201D</p><p>\u793A\u4F8B\u4E8C\uFF1A</p><p><img src="'+i+'" alt="\u56FE\u7247"></p><p>\u201C\u5E38\u91CF\u4EE3\u66FF\u9B54\u9B3C\u6570\u5B57\uFF0C\u4E3A\u4E86\u300C\u63ED\u793A\u610F\u56FE\u300D\u800C\u8FDD\u80CC\u300C\u6700\u5C11\u5143\u7D20\u300D\u3002\u201D\u8FD8\u6CA1\u7B49\u8881\u5E05\u53D1\u95EE\uFF0C\u6E05\u626C\u62A2\u5148\u56DE\u7B54\uFF0C\u5F53\u7136\u4E5F\u8D62\u4E86\u8881\u5E05\u7684\u5927\u62C7\u6307\uFF08\u5411\u4E0A\u7684\uFF09\u3002</p><p>\u4E00\u6643\u5C3108:55\u4E86\uFF0C\u8881\u5E05\u89C1Jeany\u671D\u4ED6\u8D70\u6765\uFF0C\u642D\u8F7D\u7740\u4E00\u526F\u5373\u5C06\u8981\u5F00\u4F1A\u7684\u773C\u795E\uFF0C\u4FBF\u8D77\u8EAB\u51C6\u5907\u53BB\u4F1A\u8BAE\u5BA4\u8DDF\u5979\u5546\u91CF\u665A\u4E0AOOBootcamp\u6700\u540E\u4E00\u6B21\u8BFE\u7684\u5B89\u6392\u3002</p><p>\u201C\u5582\uFF0C\u6211\u8FD8\u6709\u4E00\u4E2A\u7591\u95EE...\u201D \u201C\u684C\u4E0A\u8FD8\u6709\u4E00\u5F20\u84DD\u8272\u5361\u7247\uFF0C\u4F60\u770B\u80FD\u4E0D\u80FD\u89E3\u7B54\u4F60\u7684\u7591\u95EE\u3002\u201D \u8881\u5E05\u626D\u7740\u5934\u5F97\u610F\u5730\u7ED9\u6E05\u626C\u4E00\u4E2A\u5FAE\u7B11\uFF0C\u5C31\u8DDFJeany\u8FDB\u5165\u4E86\u4F1A\u8BAE\u5BA4\u3002</p><p>\u6E05\u626C\u62FF\u8D77\u5361\u7247\u5F00\u59CB\u9605\u8BFB\uFF1A</p><p><img src="'+l+'" alt="\u56FE\u7247"></p><p>\u6E05\u626C\u5F88\u662F\u60CA\u8BB6\u8881\u5E05\u7ADF\u7136\u5982\u6B64\u61C2\u5979\uFF0C\u4E0D\u6127\u662F\u4F18\u79C0\u7684Buddy\uFF0C\u4E00\u5927\u65E9\u5F00\u542F\u4E86\u7F8E\u597D\u7684\u5DE5\u4F5C\u8282\u594F\u3002\u5979\u8BFB\u5B8C\u5361\u7247\uFF0C\u7EE7\u7EED\u9605\u8BFB\u8881\u5E05\u7559\u7ED9\u5979\u7684\u51E0\u4E2A\u4EE3\u7801\u793A\u4F8B\uFF0C15\u5206\u949F\u8FC7\u53BB\u4E86\uFF0C\u5979\u5BF9\u7B80\u5355\u8BBE\u8BA1\u7B97\u662F\u6709\u70B9\u4F53\u4F1A\u4E86\uFF0C\u62FF\u8D77\u4E00\u5F20\u7EFF\u8272\u7684\u5361\u7247\u8BA4\u771F\u5199\u4E0B\u4E86\uFF1A</p><ol><li>\u901A\u8FC7\u6D4B\u8BD5\uFF08\u4FE1\u4EF0\uFF0C\u4E0D\u53EF\u52A8\u6447\uFF09</li><li>\u6D88\u9664\u91CD\u590D\uFF08\u804C\u8D23\uFF0C\u5C3D\u804C\u5C3D\u8D23\uFF09</li><li>\u63ED\u793A\u610F\u56FE\uFF08\u521D\u8877\uFF0C\u4E0D\u5FD8\u521D\u5FC3\uFF09</li><li>\u6700\u5C11\u5143\u7D20\uFF08\u7CBE\u9AD3\uFF0C\u7CBE\u70BC\u7B80\u6D01\uFF09</li><li><strong>\u4EE5\u4E0A\u56DB\u6761\u4F18\u5148\u7EA7\u4F9D\u6B21\u964D\u4F4E\uFF08\u7075\u9B42\uFF0C\u8D4B\u4E88\u751F\u547D\uFF09</strong></li></ol><p><strong>\u7B80\u5355\u8BBE\u8BA1\u9050\u60F3</strong></p><p>\u8DDFJeany\u5F00\u5B8C\u4F1A\uFF0C\u8881\u5E05\u56DE\u5230\u5DE5\u4F4D\u4E0A\uFF0C\u770B\u5230\u6E05\u626C\u7559\u4E0B\u7684\u5361\u7247\uFF0C\u6DF1\u611F\u6B23\u6170\uFF0C\u4ED6\u6E05\u695A\u6E05\u626C\u5DF2\u7ECF\u5165\u95E8\u4E86\uFF0C\u65E5\u540ECode Review\u4E0D\u518D\u4F1A\u88AB\u603C\u5F97\u65E0\u8A00\u4EE5\u5BF9\uFF0C\u800C\u4ED6\u5E2E\u6E05\u626C\u201C\u8BA8\u56DE\u516C\u9053\u201D\u7684\u5C0F\u5FC3\u613F\u5F88\u5FEB\u5C31\u8981\u5B9E\u73B0\u3002</p><p>\u6B64\u65F6\uFF0C\u4ED6\u5750\u4E0B\u6765\u559D\u4E86\u53E3\u6C34\uFF0C\u53D1\u51FA\u4E86\u611F\u6168 -- Kent Beck \u63D0\u51FA\u7684\u7B80\u5355\u8BBE\u8BA1\u539F\u5219\u66F4\u591A\u5173\u6CE8\u7684\u662F\u4EE3\u7801\u8BBE\u8BA1\uFF0C\u7B80\u5355\u8BBE\u8BA1\u601D\u60F3\u5176\u5B9E\u4E5F\u80FD\u8FD0\u7528\u5728\u67B6\u6784\u8BBE\u8BA1\u3001\u6C9F\u901A\u534F\u4F5C\u4E0A\u3002</p><p><strong>\u67B6\u6784\u8BBE\u8BA1</strong></p><ul><li>\u6211\u4EEC\u5E94\u8BE5\u6700\u5148\u8003\u8651\u7684\u662F\u6EE1\u8DB3\u4E1A\u52A1\u7684\u7CFB\u7EDF\u67B6\u6784\uFF08\u901A\u8FC7\u6D4B\u8BD5\uFF1A\u6027\u80FD\u3001\u7A33\u5B9A\u6027\u7B49\uFF09\u3002</li><li>\u501F\u52A9DDD\u6765\u5408\u7406\u7684\u5212\u5206\u5FAE\u670D\u52A1\uFF08\u63ED\u793A\u610F\u56FE\uFF1A\u660E\u786E\u9650\u754C\u4E0A\u4E0B\u6587\uFF09\u3002</li><li>\u63D0\u53D6\u516C\u5171\u670D\u52A1\u7EC4\u4EF6\u6765\u5206\u79BB\u5173\u6CE8\u70B9\uFF08\u6D88\u9664\u91CD\u590D\uFF1AAPI Gateway\u3001BFF\u7B49\uFF09\u3002</li><li>\u6700\u540E\uFF0C\u6211\u4EEC\u5728\u6EE1\u8DB3\u4E86\u524D\u4E09\u70B9\u7684\u524D\u63D0\u4E0B\u5C3D\u53EF\u80FD\u7B80\u5316\u7CFB\u7EDF\u67B6\u6784\u4E2D\u7684\u7EC4\u4EF6\uFF08\u6700\u5C11\u5143\u7D20\uFF09\u3002</li></ul><p><strong>\u6C9F\u901A\u534F\u4F5C</strong></p><ul><li>\u5728\u4E0E\u5BA2\u6237\u6B63\u5F0F\u573A\u5408\u7684\u6C9F\u901A\u4E2D\uFF0C\u6211\u4EEC\u59CB\u7EC8\u5E94\u8BE5\u660E\u786E\u6C9F\u901A\u4E3B\u9898\uFF0C\u786E\u5B9A\u76EE\u6807\uFF08\u901A\u8FC7\u6D4B\u8BD5 \uFF09\u3002</li><li>\u901A\u8FC7\u52A0\u5F3A\u7ED3\u6784\u601D\u8003\u529B\u6765\u63D0\u5347\u8868\u8FBE\u7684\u7ED3\u6784\u6027\u548C\u6E05\u6670\u5EA6\uFF0C\u4ECE\u800C\u8FBE\u5230\u8A00\u7B80\u610F\u8D45\uFF08\u6D88\u9664\u91CD\u590D\uFF0C\u63ED\u793A\u610F\u56FE \uFF09\u3002</li><li>\u6700\u540E\uFF0C\u6211\u4EEC\u8FBE\u5230\u4E86\u524D\u9762\u4E09\u70B9\u4E4B\u540E\u5C3D\u91CF\u4E0D\u8BF4\u591A\u4F59\u7684\u5E9F\u8BDD\uFF08\u6700\u5C11\u5143\u7D20\uFF09\u3002</li></ul><p><strong>\u7B80\u5355\u4E0D\u4EC5\u5982\u6B64</strong></p><p>\u7B80\u5355\u8BBE\u8BA1\u4E94\u539F\u5219\u4E2D\uFF0C\u6D4B\u8BD5\u8981\u786E\u4FDD\u901A\u8FC7\uFF08\u6EE1\u8DB3\u9700\u6C42\uFF09\u3001\u91CD\u590D\u5E94\u8BE5\u88AB\u6D88\u9664\u3001\u5143\u7D20\u6CA1\u5FC5\u8981\u5C31\u4E0D\u8981\u5B58\u5728\uFF0C\u8FD9\u51E0\u6761\u770B\u8D77\u6765\u76F8\u5BF9\u5177\u4F53\uFF0C\u800C\u4E14\u80FD\u89C1\u5B57\u5982\u610F\u3002\u4F46\u63ED\u793A\u610F\u56FE\u8FD9\u6837\u4E00\u4E2A\u6BCF\u4E2A\u4EBA\u6301\u6709\u4E0D\u4E00\u6837\u6807\u51C6\u7684\u6982\u5FF5\uFF0C\u5B83\u4EE3\u8868\u4E86\u4EE3\u7801\u7684\u53EF\u7406\u89E3\u6027\uFF0C\u53EF\u7406\u89E3\u6027\u7684\u53C2\u8003\u5219\u8981\u56DE\u5230\u4E1A\u52A1\u6E90\u5934\uFF0C\u662F\u5426\u51C6\u786E\u8868\u8FBE\u4E86\u4E1A\u52A1\u6982\u5FF5\u3002<strong>\u6700\u540E\uFF0C\u4F18\u5148\u7EA7\u539F\u5219\u662F\u4E07\u4E07\u4E0D\u53EF\u5FFD\u7565\u7684\uFF0C\u5426\u5219\u8FD9\u4E2A\u6846\u67B6\u5C31\u5931\u53BB\u4E86\u7075\u9B42\u548C\u751F\u547D\u529B\u3002</strong></p><p>\u8881\u5E05\u505A\u4E86\u591A\u5E74\u7684\u8F6F\u4EF6\u5F00\u53D1\u548C\u57F9\u8BAD\uFF0C\u4ED6\u5FC3\u91CC\u5F88\u6E05\u695A\uFF0C\u90A3\u4E2A\u5B8C\u7F8E\u7684\u7B54\u6848\u53EF\u80FD\u4E0D\u5B58\u5728\u3002\u8F6F\u4EF6\u5F00\u53D1\u662F\u4E00\u79CD\u77E5\u8BC6\u5DE5\u4F5C\uFF0C\u8BBE\u8BA1\u53C8\u662F\u4EC1\u8005\u89C1\u4EC1\u667A\u8005\u89C1\u667A\uFF0C\u7B80\u5355\u8BBE\u8BA1\u4E94\u539F\u5219\u80FD\u5728\u4E00\u5B9A\u7A0B\u5EA6\u4E0A\u5E2E\u52A9\u7A0B\u5E8F\u5458\u5C11\u8D70\u5F2F\u8DEF\u3002</p><p>\u9664\u4E86\u8FD9\u4E9B\uFF0C\u5728\u56E2\u961F\u793E\u4EA4\u6D3B\u52A8\u53D1\u751F\u63A2\u8BA8\u662F\u4E00\u4E2A\u975E\u5E38\u6709\u6548\u7684\u9014\u5F84\u3002\u8FD9\u4E5F\u662F\u4ED6\u5982\u6B64\u91CD\u89C6\u5728\u5DE5\u4F5C\u574A\u4E2D\u5F15\u5165\u793E\u4EA4\u6D3B\u52A8\u7684\u539F\u56E0\u3002\u4EE3\u7801\u662F\u5426\u6613\u8BFB\u61C2\uFF0C\u9664\u4E86\u81EA\u6211\u5BA1\u89C6\uFF0C\u8FD8\u9700\u8981\u591A\u51E0\u4E2A\u5927\u8111\uFF0C\u6BD4\u5982\uFF1ACode Review\u3001\u7ED3\u5BF9\u7F16\u7A0B\u3002</p><p>\u539F\u6587\uFF1A<a href="https://mp.weixin.qq.com/s/bgRC3T_yykbb8oJ9XuieJA" target="_blank" rel="noopener noreferrer">https://mp.weixin.qq.com/s/bgRC3T_yykbb8oJ9XuieJA</a></p>',69),_=[g];function c(m,d,u,h,f,k){return s(),t("div",null,_)}var y=p(a,[["render",c]]);export{v as __pageData,y as default};
